DROP DATABASE IF EXISTS `hackathon`;
CREATE DATABASE `hackathon`;
USE `hackathon`;

CREATE TABLE `user` (
  `email` varchar(50) PRIMARY KEY,
  `first_name` varchar(50),
  `last_name` varchar(50),
  `password` varchar(50)
);




CREATE TABLE `user_images` (
  `img_id` integer PRIMARY KEY,
  `user_id` varchar(50),
  `img_url` varchar(200)
);


CREATE TABLE trail(
   trail_id      INTEGER  NOT NULL PRIMARY KEY 
  ,name          VARCHAR(100) NOT NULL
  ,length_3d     VARCHAR(18) NOT NULL
  ,max_elevation VARCHAR(18)
  ,min_elevation VARCHAR(19)
  ,uphill        VARCHAR(18) NOT NULL
  ,downhill      VARCHAR(21) NOT NULL
  ,difficulty    VARCHAR(40) NOT NULL
  ,country       VARCHAR(22)
  ,continent     VARCHAR(13)
);



ALTER TABLE `user_images` ADD FOREIGN KEY (`user_id`) REFERENCES `user` (`email`);
